import { Bar } from 'vue-chartjs';
import { Chart as ChartJS, Title, Tooltip, Legend, BarElement, CategoryScale, LinearScale } from 'chart.js';

// Register Chart.js components
ChartJS.register(Title, Tooltip, Legend, BarElement, CategoryScale, LinearScale);
export default {
  name: 'FiveDayForecast',
  components: {
    Bar
  },
  props: {
    forecastData: {
      type: Array,
      required: true // Ensure forecastData is always passed
    },
    location: {
      type: String,
      default: 'Your Location' // Default location if not provided
    }
  },
  data() {
    return {
      chartData: {
        labels: [],
        // Days of the week
        datasets: [{
          label: 'Temperature (°F)',
          data: [],
          // Temperatures for the next 5 days
          backgroundColor: 'rgba(75, 192, 192, 0.5)',
          // Bar fill color
          borderColor: 'rgb(75, 192, 192)',
          // Bar border color
          borderWidth: 1
        }]
      },
      chartOptions: {
        responsive: true,
        plugins: {
          legend: {
            position: 'top'
          },
          tooltip: {
            callbacks: {
              label: tooltipItem => {
                return `${tooltipItem.raw} °F`;
              }
            }
          }
        },
        scales: {
          x: {
            title: {
              display: true,
              text: 'Day'
            }
          },
          y: {
            title: {
              display: true,
              text: 'Temperature (°F)'
            },
            min: 0,
            max: 100 // Adjust based on expected temperature range
          }
        }
      }
    };
  },
  methods: {
    processForecastData() {
      const temperatures = [];
      const days = new Set();

      // Process forecastData for unique days
      this.forecastData.forEach(item => {
        const date = new Date(item.dt * 1000); // Convert timestamp to local date
        const day = date.toLocaleDateString('en-US', {
          weekday: 'long'
        });

        // Avoid duplicates for the same day
        if (!days.has(day)) {
          days.add(day);
          temperatures.push(item.main.temp);
        }
      });

      // Update chart data
      this.chartData.labels = Array.from(days);
      this.chartData.datasets[0].data = temperatures;
    }
  },
  created() {
    if (this.forecastData && this.forecastData.length > 0) {
      this.processForecastData();
    } else {
      console.error('No forecast data provided');
    }
  }
};
