import { createRouter, createWebHistory } from 'vue-router';
import FiveDayView from '@/views/5Day.vue';  // The 5-day forecast page
import MySavedLocationsView from '@/views/MySavedLocations.vue';  

const routes = [
  {
    path: '/5day/:location',  // Route for 5-day forecast page
    name: 'FiveDayView',
    component: FiveDayView,
    props: true,  // Pass route params as props to the component
  },
  { 
    path: '/saved', 
    name: 'MySavedLocationsView', 
    component: MySavedLocationsView 
  },
];

const router = createRouter({
  history: createWebHistory(),
  routes,
});

export default router;
