import { createRouter, createWebHistory } from 'vue-router';
import FiveDay from '@/views/5Day.vue';
import HourlyForecast from '@/views/HourlyForecast.vue';
import MySavedLocations from '@/views/MySavedLocations.vue';

const routes = [
  { path: '/', redirect: '/forecast/default-location' }, // Redirect to a default location
  { path: '/forecast/:location', name: 'FiveDayForecast', component: FiveDay, props: true },
  { path: '/hourly/:location', name: 'HourlyForecast', component: HourlyForecast, props: true },
  { path: '/saved-locations', name: 'SavedLocations', component: MySavedLocations },
  // Catch-all route for 404 page
  { path: '/:pathMatch(.*)*', name: 'NotFound', component: () => import('@/views/NotFound.vue') }
];

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes,
});

export default router;
