import { Bar } from 'vue-chartjs';
import { Chart as ChartJS, Title, Tooltip, Legend, LineElement, CategoryScale, LinearScale } from 'chart.js';

// Register the required components in Chart.js
ChartJS.register(Title, Tooltip, Legend, LineElement, CategoryScale, LinearScale);
export default {
  name: 'FiveDayForecast',
  components: {
    Line
  },
  props: {
    forecastData: Array,
    // Receive forecast data from parent
    location: String // Receive location from parent
  },
  data() {
    return {
      chartData: {
        labels: [],
        // will hold the days
        datasets: [{
          label: 'Temperature (째F)',
          // Label for the dataset
          data: [],
          // Temperature data points for the 5 days
          fill: false,
          // No fill under the line
          borderColor: 'rgb(75, 192, 192)',
          // Line color
          tension: 0.1 // Line smoothness
        }]
      },
      chartOptions: {
        responsive: true,
        plugins: {
          legend: {
            position: 'top'
          }
        },
        scales: {
          x: {
            title: {
              display: true,
              text: 'Day'
            }
          },
          y: {
            title: {
              display: true,
              text: 'Temperature (째F)'
            },
            min: 0,
            max: 100 // Adjust according to expected temperature range
          }
        }
      }
    };
  },
  methods: {
    processForecastData() {
      const temperatures = [];
      const days = [];

      // Loop through the forecast data and collect temperatures for the 5 days
      this.forecastData.forEach(item => {
        const date = new Date(item.dt * 1000); // Convert to local date
        const day = date.getDay();

        // Collect data for the next 5 days
        if (!days.includes(day)) {
          days.push(day);
          temperatures.push(item.main.temp);
        }
      });

      // Update chart data
      this.chartData.labels = days.map(dayIndex => {
        const date = new Date();
        date.setDate(date.getDate() + dayIndex);
        return date.toLocaleDateString('en-US', {
          weekday: 'long'
        });
      });
      this.chartData.datasets[0].data = temperatures;
    }
  },
  created() {
    if (this.forecastData.length > 0) {
      this.processForecastData(); // Process forecast data passed from the parent
    } else {
      console.error('No forecast data available');
    }
  }
};
