import { createRouter, createWebHistory } from 'vue-router';
import FiveDayForecast from '../views/5Day.vue';
import MySavedLocations from '../views/MySavedLocations.vue';

const routes = [
  {
    path: '/weather/:location',
    name: 'FiveDayForecast',
    component: FiveDayForecast,
    props: (route) => ({ location: route.query.location }),  // Pass the location as a prop to the component

  },
  {
    path: '/saved-locations',
    name: 'SavedLocations',
    component: MySavedLocations,
  },
];

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes,
});

export default router;
